mapper.select:
SELECT MEMBER_NO, MEMBER_PW, MEMBER_NAME, MEMBER_PHONE, MEMBER_CLASS, ALLOW_DATE
FROM MEMBER
WHERE MEMBER_NO = #{memberNo} AND MEMBER_PW = #{memberPw}

SELECT MEMBER_NO, MEMBER_NAME, MEMBER_PHONE, MEMBER_CLASS, ALLOW_DATE, DORMANCY_STATUS
FROM MEMBER
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER SET MEMBER_PW = #{memberPw},
MEMBER_NAME = #{memberName},
MEMBER_PHONE = #{memberPhone},
MEMBER_CLASS = #{memberClass},
DORMANCY_STATUS = #{dormancyStatus}
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT (
    SELECT COUNT(*)
    FROM MEMBER
    WHERE MEMBER_NO = #{memberNo}
    ) + (
    SELECT COUNT(*)
    FROM JOIN
    WHERE JOIN_NO = #{memberNo}
    )
FROM DUAL
